import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

export default function RealityScoreScreen({ route }) {
  const { influencer } = route.params;

  return (
    <View style={styles.container}>
      <Text style={styles.name}>@{influencer.handle}</Text>
      <Text style={styles.score}>Reality Score: {influencer.trustScore}</Text>
      <Text style={styles.tier}>Trust Tier: {influencer.trustTier}</Text>
      <Text style={styles.platform}>Platform: {influencer.platform}</Text>

      <Text style={styles.sectionTitle}>Metrics</Text>
      <Text>Followers: {influencer.metrics?.followers?.toLocaleString()}</Text>
      <Text>Engagement Rate: {influencer.metrics?.engagementRate}%</Text>

      <Text style={styles.sectionTitle}>Red Flags</Text>
      {influencer.redFlags?.length > 0 ? (
        influencer.redFlags.map((flag, i) => <Text key={i}>⚠️ {flag}</Text>)
      ) : (
        <Text>No red flags detected</Text>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20 },
  name: { fontSize: 24, fontWeight: 'bold', marginVertical: 10 },
  score: { fontSize: 32, color: '#27AE60', fontWeight: 'bold' },
  tier: { fontSize: 20, color: '#F39C12', marginVertical: 5 },
  platform: { fontSize: 16, color: '#555' },
  sectionTitle: { fontSize: 18, fontWeight: '600', marginVertical: 10 }
});
